import React, { Component } from 'react';
import {ProductConsumer} from '../context';


export default class Details extends Component {
    render() {
        return (
            <ProductConsumer>
                {value=> {
                    const {id, title, price, img,inCart, info} = value.detailProduct;
                    return (
                      <div className="container col-10 mx-auto col-md-6">
                        <div className="row">
                          ,
                          <img src={img} alt={title} />
                        </div>

                        <div className="row">
                          <h1>{title}</h1>
                        </div>
                      </div>
                    );
                
                }}
            </ProductConsumer>
        )
    }
}
