import React, { Component } from "react";
import { ProductConsumer } from "../context";
import CartColumns from "./CartColumns";

export default class Cart extends Component {
  render() {
    return (
      <ProductConsumer>
        {value => {
          const { cart } = value;
          if (cart.length > 0) {
            return (
              <React.Fragment>
                <h1 className="title">Your Cart</h1>
                <CartColumns />
              </React.Fragment>
            );
          } else {
            return <h1 className="text-center m-5 p-5 text-primary">Your cart is empty!</h1>;
          }
        }}
      </ProductConsumer>
    );
  }
}
