import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";

export default function CartItem({ item }) {
	const {id,img,title,price,count,total} = item;
	const {incrementCount,decrementCount, clearCartItem, resetCart} = value;
  return (
    <div className="row my-1 text-center">
      <div className="col-10 mx-auto col-md-2">
        <img
          className="img-fluid"
          src={img}
          style={{ width: "5rem", height: "5rem" }}
          alt={title}
        />
      </div>
      <div className="col-10 mx-auto col-md-2">
        <span className="d-md-none">Product: </span>
        {title}
      </div>
      <div className="col-10 mx-auto col-md-2">
        <span className="d-md-none">Price: </span>
        {price}
      </div>
      {/*  */}
      <div className="col-10 mx-auto col-md-2 my-2 my-lg-0">
        <div className="d-flex justify-content-center">
          <div>
            <span className="btn btn-black mx-1" onClick={()=>incrementCount(id)}> - </span>
            <span className="mx-1">{count}</span>
            <span className="btn btn-black mx-1"> + </span>
          </div>
        </div>
      </div>
      {/*  */}
      <div className="col-10 mx-auto col-md-2">
        <FontAwesomeIcon icon={faTrash} />
      </div>
      <div className="col-10 mx-auto col-md-2">
        <p>{total}</p>
      </div>
    </div>
  );
}
