import React from "react";

export default function CartTotals({ value }) {
  const { cartSubTotal, cartTax, cartTotal, resetCart } = value;
  return (
    <React.Fragment>
      <div className="container">
        <div className="row">
          <div className="w-100 d-flex flex-column justify-content-end align-items-center">
            <button
              className="btn btn-outline-danger"
              onClick={() => resetCart()}
            >
              Clear Cart
            </button>
            <span className="">Sub-total: {cartSubTotal}</span>
            <span className="">Tax: {cartTax}</span>{" "}
            <span className="">Total: {cartTotal}</span>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}
