import styled from "styled-components";
export const ButtonElement = styled.button`
  background: none;
  border: ${props => (props.logo ? "none" : ".05rem solid ")}
  color: ${props => (props.logo ? "#000" : "#fff")}
  padding: .5rem;
  font-size: 1.3rem
  outline:none;
  &:hover{
    color:  ${props => (props.cart ? "#fff" : "var(--mainBrown)")};
  }
`;
