import React, { Component } from "react";
import styled from "styled-components";
import { ProductConsumer } from "../context";
import { Link } from "react-router-dom";
import { ButtonElement } from "./Button";

export default class Modal extends Component {
  render() {
    return (
      <ProductConsumer>
        {value => {
          const { img, title, price } = value.modalProduct;
          return (
            <ModalContainer>
              <div className="container">
                <div className="row">
                  <div
                    id="modal"
                    className="col-8 mx-auto col-md-6 col-lg-4 text-center p-5"
                  >
                    <h4 className=" text- center">Item added to Cart!</h4>
                  </div>
                  <img className="img-fluid" src={img} alt={title} />
                    <h5>{title}</h5>
                    <h5>Price :${price}</h5>
                    <Link to="/">
                      <ButtonElement>Back to Products</ButtonElement>
                    </Link>
                    <Link to="/cart">
                      <ButtonElement>View CART</ButtonElement>
                    </Link>
                </div>
              </div>
            </ModalContainer>
          );
        }}
      </ProductConsumer>
    );
  }
}
const ModalContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  // background: #000;
  display:flex;
  align-items:center;
  justify-content:center;
  #modal{
    background: #fff
  }
`;
